package util;import util.Particle;import java.util.ArrayList;/** * Created by Ishiwatari on 2016/08/12. */public abstract class SwarmIntelligence {    ArrayList<Particle> ParticleList;    int count = 100000;    SwarmIntelligence() {    }    Particle run() {        for (int i = 0; i < count; i++) {            step();        }        Particle ans = ParticleList.get(0);        for (Particle p : ParticleList) {            if(p.distance(new Particle(0,0)) < ans.distance(new Particle(0,0))){                ans = p;            }        }        return ans;    }    abstract int ObjectiveFunction(ArrayList<Particle> pList);    abstract void step();}